INSERT INTO `creature_text` (`Entry`, `GroupID`, `ID`, `Text`, `Type`, `Language`, `Probability`, `Emote`, `Duration`, `Sound`, `BroadcastTextID`, `comment`) VALUES
(61620, 0, 0, 'Спасибо, конечно, за спасение, но мы по-прежнему со всех сторон окружены богомолами.', 12, 0, 100, 0, 0, 31505, 61584, 'Ян Железный Коготь to Сик''тик-страж'),
(61620, 1, 0, 'Только посмотрите. Их слишком много. Мы не справимся.', 12, 0, 100, 0, 0, 31506, 61591, 'Ян Железный Коготь to Сик''тик-страж'),
(61620, 2, 0, 'Хм-м... У меня появилась идея. Это может сработать, но мне понадобится помощь!', 12, 0, 100, 0, 0, 31507, 62580, 'Ян Железный Коготь to Сик''тик-страж'),
(61620, 3, 0, 'Обратим оружие этих богомолов против них самих!', 12, 0, 100, 0, 0, 31508, 62581, 'Ян Железный Коготь to Сик''тик-страж'),
(61620, 4, 0, 'Ли! Ло! Нам нужен сок!', 12, 0, 100, 0, 0, 31509, 62582, 'Ян Железный Коготь to Player'),
(61620, 5, 0, 'Ну, начнем веселье?', 12, 0, 100, 0, 0, 31510, 62583, 'Ян Железный Коготь to Player'),
(61620, 6, 0, 'Мы раздавим вас, как жуков!', 14, 0, 100, 0, 0, 31514, 66224, 'Ян Железный Коготь to Player'),
(61620, 7, 0, 'Большой отряд |cFF00dc29сик''тиков-роевиков|r вступает в битву.', 41, 0, 100, 0, 0, 30265, 62318, 'Ян Железный Коготь to Player'),
(61620, 8, 0, 'Роевики взбираются по склону! Используй смолу, чтобы их замедлить!', 12, 0, 100, 0, 0, 31511, 62586, 'Ян Железный Коготь to Player'),
(61620, 9, 0, 'Желтокрыл приближается. Берегитесь!', 12, 0, 100, 0, 0, 31503, 62023, 'Ян Железный Коготь to Player'),
(61620, 10, 0, 'Большой отряд |cFFB600B8сик''тиков-разрушителей|r вступает в бой.', 41, 0, 100, 0, 0, 30266, 62578, 'Ян Железный Коготь to Player'),
(61620, 11, 0, 'У подрывников бомбы! Прямой – и они взлетят на воздух!', 12, 0, 100, 0, 0, 31504, 62025, 'Ян Железный Коготь to Player'),
(61620, 12, 0, 'Большой отряд |cFF00dc29сик''тиков-роевиков|r и |cFF815d18сик''тиков-воинов|r вступает в битву.', 41, 0, 100, 0, 0, 30267, 62622, 'Ян Железный Коготь to Player'),
(61620, 13, 0, 'Воин! Сосредоточьте огонь на нем!', 12, 0, 100, 0, 0, 31512, 62168, 'Ян Железный Коготь to Player'),
(61620, 14, 0, 'Большой отряд |cFFB600B8сик''тиков-разрушителей|r и |cFF815d18сик''тиков-воинов|r вступает в бой.', 41, 0, 100, 0, 0, 30268, 62623, 'Ян Железный Коготь to Player'),
(61620, 15, 0, 'По-моему, было весело!', 12, 0, 100, 0, 0, 31497, 62551, 'Ян Железный Коготь to Player'),
(61620, 16, 0, 'Ли, Ло! Спуститесь к нашим друзьям!', 12, 0, 100, 0, 0, 31498, 62734, 'Ян Железный Коготь to Player'),
(61620, 17, 0, 'Ворота заперты... но думаю, я смогу их открыть.', 12, 0, 100, 0, 0, 31499, 62735, 'Ян Железный Коготь to Player'),
(61620, 18, 0, 'Дайте-ка подумать... запал на 30 секунд...', 12, 0, 100, 0, 0, 31500, 62736, 'Ян Железный Коготь to Player'),
(61620, 19, 0, 'Что? Я сказал "тридцать?" Я имел в виду три!', 12, 0, 100, 0, 0, 31501, 62737, 'Ян Железный Коготь to Player'),
(61620, 20, 0, 'Быстрее! Вы должны остановить осаду!', 12, 0, 100, 0, 0, 31502, 62738, 'Ян Железный Коготь to Player');
